; // Флаг, указывающий, разрешено ли движение по оси Z csharp Copy code private Vector3 startPosition; // Начальная позиция платформы private Vector3 targetPosition; // Целевая позиция платформы void Start() { startPosition = transform.position; Vector3 moveDirection = Vector3.zero; if (moveX) moveDirection += Vector3.right; if (moveY) moveDirection += Vector3.up; if (moveZ) moveDirection += Vector3.forward; targetPosition = startPosition + (moveDirection.normalized * moveDistance); StartCoroutine(MovePlatform()); } IEnumerator MovePlatform() { while (true) { yield return StartCoroutine(MoveToTargetPosition()); yield return new WaitForSeconds(pauseDuration); yield return StartCoroutine(MoveToStartPosition()); yield return new WaitForSeconds(pauseDuration); } } IEnumerator MoveToTargetPosition() { float elapsedTime = 0f; while (elapsedTime < moveSpeed) { float t = elapsedTime / moveSpeed; transform.position = Vector3.Lerp(startPosition, targetPosition, t); elapsedTime += Time.deltaTime; yield return null; } transform.position = targetPosition; } IEnumerator MoveToStartPosition() { float elapsedTime = 0f; while (elapsedTime < moveSpeed) { float t = elapsedTime / moveSpeed; transform.position = Vector3.Lerp(targetPosition, startPosition, t); elapsedTime += Time.deltaTime; yield return null; } transform.position = startPosition; } } сделай что бы игрок используя character controller прилипал к платформе пока стоит на ней","completion":"Вот как вы можете изменить код, чтобы прилипание происходило, когда игрок стоит на платформе: \`\`\`csharp using System.Collections; using System.Collections.Generic; using UnityEngine; public class MovingPlatform : MonoBehaviour { public float moveDistance = 10f; public float moveSpeed = 3f; public float pauseDuration = 2f; public bool moveX = true; public bool moveY = false; public bool moveZ = false; private Vector3 startPosition; private Vector3 targetPosition; private CharacterController characterController; private bool isPlayerOnPlatform = false; void Start() { startPosition = transform.position; Vector3 moveDirection = Vector3.zero; if (moveX) moveDirection += Vector3.right; if (moveY) moveDirection += Vector3.up; if (moveZ) moveDirection += Vector3.forward; targetPosition = startPosition + (moveDirection.normalized * moveDistance); characterController = GetComponent<CharacterController>(); StartCoroutine(MovePlatform()); } void FixedUpdate() { if (isPlayerOnPlatform) { characterController.Move(transform.position - startPosition); } } IEnumerator MovePlatform() { while (true) { yield return StartCoroutine(MoveToTargetPosition()); yield return new WaitForSeconds(pauseDuration); yield return StartCoroutine(MoveToStartPosition()); yield return new WaitForSeconds(pauseDuration); } } IEnumerator MoveToTargetPosition() { float elapsedTime = 0f; while (elapsedTime < moveSpeed) { float t = elapsedTime / moveSpeed; transform.position = Vector3.Lerp(startPosition, targetPosition, t); elapsedTime += Time.deltaTime; yield return null; } transform.position = targetPosition; } IEnumerator MoveToStartPosition() { float elapsedTime = 0f; while (elapsedTime < moveSpeed) { float t = elapsedTime / moveSpeed; transform.position = Vector3.Lerp(targetPosition, startPosition, t); elapsedTime += Time.deltaTime; yield return null; } transform.position = startPosition; } void OnTriggerEnter(Collider other) { if (other.CompareTag(\\"Player\\")) { isPlayerOnPlatform = true; } } void OnTriggerExit(Collider other) { if (other.CompareTag(\\"Player\\")) { isPlayerOnPlatform = false; } } } \`\`\` Добавлены следующие изменения: - Добавлена переменная \`characterController\`, чтобы получить доступ к скрипту CharacterController игрока. - В методе \`FixedUpdate\` добавлено перемещение игрока с платформы, когда он находится на платформе. - Добавлены методы \`OnTriggerEnter\` и \`OnTriggerExit\`, чтобы установить флаг \`isPlayerOnPlatform\` в true/false при входе и выходе игрока с платформы."},{"prompt":"нужен скрипт при касании 