; // Remove duplicates results = results.filter((result, index, self) => index === self.findIndex(r => ( r.name === result.name && r.genre === result.genre && r.city === result.city )) ); } return results; } (он работает) а вот то что сделал и то что дает кучу ошибок: function search(term, role, city, genre, callback) { let query = term.toLowerCase(); const queryValues = [ \`%{query}%\`, role === '' ? null : \`%{role}%\`, city === '' ? null : city.toLowerCase().trim(), genre === '' ? '%' : \`%{genre}%\` ]; const sql = \`SELECT * FROM users WHERE (name LIKE ? OR genre LIKE ? OR genre IS NULL) AND (role LIKE ? OR role IS NULL) AND (city = ? OR city IS NULL) AND (genre LIKE ? OR genre IS NULL)\`; connection.query(sql, queryValues, (err, result) => { if (err) { console.error('Ошибка при поиске: ', err); callback(err, []); } else { let results = result.map(musician => { return { name: musician.name, genre: musician.genre, originalName: musician.name, profileLink: \`/profile/{musician.id}\`, thumbnail: musician.thumbnail, soundcloud: musician.soundcloud, role: musician.role, city: musician.city }; }).sort((a, b) => { const aNameScore = a.name.toLowerCase().startsWith(query) ? 2 : a.name.toLowerCase().includes(query) ? 1 : 0; const bNameScore = b.name.toLowerCase().startsWith(query) ? 2 : b.name.toLowerCase().includes(query) ? 1 : 0; const aGenreScore = a.genre.toLowerCase().startsWith(query) ? 2 : a.genre.toLowerCase().includes(query) ? 1 : 0; const bGenreScore = b.genre.toLowerCase().startsWith(query) ? 2 : b.genre.toLowerCase().includes(query) ? 1 : 0; // Sort by name score, then genre score, then location score (descending) if (aNameScore + aGenreScore + a.city < bNameScore + bGenreScore + b.city) { return 1; } else if (aNameScore + aGenreScore + a.city > bNameScore + bGenreScore + b.city) { return -1; } else { return 0; } }); // Remove duplicates results = results.filter((result, index, self) => index === self.findIndex(r => ( r.name === result.name && r.genre === res